import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'
import startNodeImg from '../images/start.png'
import targetNodeImg from '../images/target.png'
import obstacleNodeImg from '../images/obstacle.png'

const Node = ({ nodeId, size, gridState, setGridState, draggingSelection, setDraggingSelection }) => {
    const [type, setType] = useState(gridState[nodeId]);
    const nodeDim = {
        width: size,
        height: size
    };

    const imgDim = {
        width: size * 0.8,
        height: size * 0.8
    };

    const mouseDowned = () => {
        const currNodeType = gridState[nodeId][0];

        if (currNodeType === getNodeTypeEnum('none')) {
            setDraggingSelection({ [getNodeTypeEnum('obstacle')]: true });
            setGridState({ [nodeId]: [getNodeTypeEnum('obstacle'), ...gridState[nodeId]] })
            return;
        }

        if (currNodeType === getNodeTypeEnum('obstacle')) {
            setType(type.slice(1))
            return;
        }

        if (gridState[nodeId][0] === getNodeTypeEnum('start')) {
            setDraggingSelection({ 'start': true });
            return;
        }

        if (gridState[nodeId][0] === getNodeTypeEnum('target')) {
            setSelectingTarget(true);
            return;
        }


    };

    const mouseEntered = () => {
        if (selectingStart) {
            setType([getNodeTypeEnum('start'), ...type]);
            return;
        }

        if (selectingTarget) {
            setType([getNodeTypeEnum('target'), ...type]);
            return;
        }

        if (selectingObstacle && type[0] === getNodeTypeEnum('none')) {
            setType([getNodeTypeEnum('obstacle'), ...type]);
            return;
        }
    };

    const mouseLeft = () => {
        if (selectingStart || selectingTarget) {
            setType(type.slice(1));
            return;
        }
    };

    const mouseUped = () => {
        if (selectingStart) {
            setSelectingStart(false);
            return;
        }

        if (selectingTarget) {
            setSelectingTarget(false);
            return;
        }

        if (selectingObstacle) {
            setSelectingObstacle(false);
        }
    };

    return (
        <div style={nodeDim} className="node" >
            {gridState[nodeId][0] === getNodeTypeEnum('start') && <img className='img-node' alt='Start Node' style={imgDim} src={startNodeImg} />}
            {gridState[nodeId][0] === getNodeTypeEnum('target') && <img className='img-node' alt='Target Node' style={imgDim} src={targetNodeImg} />}
            {gridState[nodeId][0] === getNodeTypeEnum('obstacle') && <img className='img-node' alt='Obstacle' style={imgDim} src={obstacleNodeImg} />}
            <div style={{ ...nodeDim, position: 'absolute', top: 0, left: 0 }} onMouseDown={mouseDowned} onMouseUp={mouseUped} onMouseEnter={mouseEntered} onMouseLeave={mouseLeft} />
        </div>
    )
}

export default Node
