import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'

const Node = ({ size, nodeType, selectingStart, setSelectingStart }) => {
    const [type, setType] = useState(nodeType)
    let nodeDim = {
        width: size,
        height: size,
    }

    const getBackgroundColor = () => {
        if (type == getNodeTypeEnum.start) {
            return 'green'
        }
        if (type == getNodeTypeEnum.target) {
            return 'red'
        }
        if (type == getNodeTypeEnum.empty) {
            return 'none'
        }
    }
    const mouseDown = () => {
        console.log("MOUSE DOWN")
        if (type == getNodeTypeEnum.start) {
            setSelectingStart(true);
            return;
        }

        if (selectingStart) {
            setType(getNodeTypeEnum.start)
        }
    };

    return (
        <div style={{ ...nodeDim, backgroundColor: getBackgroundColor() }} className="node" onMouseDown={mouseDown} />
    )
}

export default Node
