import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'
import startNodeImg from '../images/start.png'
import targetNodeImg from '../images/target.png'

const Node = ({ size, nodeType, selectingStart, setSelectingStart, selectingTarget, setSelectingTarget }) => {
    const [type, setType] = useState(nodeType);
    let nodeDim = {
        width: size,
        height: size,
    };

    const getBackgroundColor = () => {
        if (type === getNodeTypeEnum('start')) {
            return 'green';
        }
        if (type === getNodeTypeEnum('target')) {
            return 'red';
        }
        if (type === getNodeTypeEnum('empty')) {
            return 'transparent';
        }
    };

    const mouseDowned = () => {
        if (type === getNodeTypeEnum('start')) {
            setSelectingStart(true);
            return;
        }

        if (type === getNodeTypeEnum('target')) {
            setSelectingTarget(true);
            return;
        }
    };

    const mouseEntered = () => {
        if (selectingStart) {
            setType(getNodeTypeEnum('start'));
            return;
        }

        if (selectingTarget) {
            setType(getNodeTypeEnum('target'));
            return;
        }
    };

    const mouseLeft = () => {
        if (selectingStart || selectingTarget) {
            setType(getNodeTypeEnum('empty'));
            return;
        }
    };

    const mouseUped = () => {
        if (selectingStart) {
            setSelectingStart(false);
            return;
        }
        if (selectingTarget) {
            setSelectingTarget(false);
            return;
        }
    };

    return (
        <div style={nodeDim} className="node" onMouseDown={mouseDowned} onMouseEnter={mouseEntered} onMouseLeave={mouseLeft} onMouseUp={mouseUped}>
            {type === getNodeTypeEnum("start") && <img className='img-node' style={nodeDim} src={startNodeImg} />}
            {type === getNodeTypeEnum("target") && <img className='img-node' style={nodeDim} src={targetNodeImg} />}
        </div>
    )
}

export default Node
