import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'

const Node = ({ size, nodeType, selectingStart, setSelectingStart }) => {
    const [type, setType] = useState(nodeType)
    let bColor = 'none'

    if (type == getNodeTypeEnum.start) {
        bColor = 'green'
    }
    else if (getNodeTypeEnum.target) {
        bColor = 'red'
    }

    let nodeStyle = {
        width: size,
        height: size,
        backgroundColor: bColor
    }

    const getBackgroundColor = () => {
        if (type == getNodeTypeEnum.start) {
            return 'green'
        }
        if (type == getNodeTypeEnum.target) {
            return 'red'
        }
        if (type == getNodeTypeEnum.empty) {
            return 'none'
        }
    }
    const mouseDown = () => {
        console.log("MOUSE DOWN")
        if (type == getNodeTypeEnum.start) {
            setSelectingStart(true);
            return;
        }

        if (selectingStart) {
            setType(getNodeTypeEnum.start)
        }
    };

    return (
        <div style={nodeStyle} className="node" onMouseDown={mouseDown} />
    )
}

export default Node
