import Grid from './components/Grid.js'

function App() {
  console.log(window.innerHeight, window.innerWidth)
  const nodeSize = 50;
  const winHeight = window.innerHeight - 150;
  const rows = parseInt(winHeight / nodeSize) - 4; // leave space on top and bottom of grid
  const cols = parseInt(window.innerWidth / nodeSize) - 2; // leave 1 node spaces on left and right of grid

  return (
    <>
      <Grid rows={rows} cols={cols} nodeSize={nodeSize} />
    </>
  );
}

export default App;
