import React from 'react'
import Node from './Node.js'
import { useState } from 'react'
import { getNodeTypeEnum } from '../utils/util.js'

const Grid = ({ rows, cols, nodeSize, gridState, setGridState, selectedObstacle }) => {
    const [selectingStart, setSelectingStart] = useState(false);
    const [selectingTarget, setSelectingTarget] = useState(false);
    const [selectingObstacle, setSelectingObstacle] = useState(false);

    let defaultDraggingSelection = {
        'start': false,
        'target': false,
        'obstacle': false
    };
    const [draggingSelection, setDraggingSelection] = useState(defaultDraggingSelection);

    // fill nodes in the grid
    const nodes = [];
    for (let x = 0; x < rows; x++) {
        for (let y = 0; y < cols; y++) {
            let type = gridState[`${x}:${y}`]

            nodes.push(<Node size={nodeSize} gridState={gridState} setGridState={setGridState} draggingSelection={draggingSelection} setDraggingSelection={setDraggingSelection} key={[x, y]} />);
        }
    }

    const gridStyle = {
        width: (cols * nodeSize) + cols,
        height: (rows * nodeSize) + rows
    };

    return (
        <div className='grid' style={gridStyle}>
            { nodes}
        </div >
    )
}

export default Grid
