import { useState } from 'react'
import React from 'react'

const Node = ({ size, isStart, isTarget, selectingStart, setSelectingStart }) => {
    const [isStartNode, setStart] = useState(isStart)
    let bColor = 'none'

    if (isStart) {
        bColor = 'green'
    }
    else if (isTarget) {
        bColor = 'red'
    }

    let nodeStyle = {
        width: size,
        height: size,
        backgroundColor: bColor
    }

    const mouseDown = () => {
        console.log("MOUSE DOWN")
        if (isStartNode) {
            setSelectingStart(true);
            return;
        }

        if (selectingStart) {
            setStart(true)
        }
    };

    return (
        <div style={nodeStyle} className="node" onMouseDown={mouseDown} />
    )
}

export default Node
