import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'
import startNodeImg from '../images/start.png'
import targetNodeImg from '../images/target.png'
import obstacleNodeImg from '../images/obstacle.png'

const Node = ({ size, nodeType, selectingStart, setSelectingStart, selectingTarget, setSelectingTarget, selectingObstacle, setSelectingObstacle }) => {
    const [type, setType] = useState([nodeType]);
    let nodeDim = {
        width: size,
        height: size,
    };

    const mouseDowned = () => {
        if (type[0] === getNodeTypeEnum('start')) {
            setSelectingStart(true);
            return;
        }

        if (type[0] === getNodeTypeEnum('target')) {
            setSelectingTarget(true);
            return;
        }

        if (type[0] === getNodeTypeEnum('none')) {
            setSelectingObstacle(true);
            type.unshift(getNodeTypeEnum('obstacle'));
            setType(type);
            return;
        }

        if (type[0] === getNodeTypeEnum('obstacle')) {
            setType(type.shift())
            return;
        }
    };

    const mouseEntered = () => {
        if (selectingStart) {
            setType(type.unshift(getNodeTypeEnum('start')));
            return;
        }

        if (selectingTarget) {
            setType(type.unshift(getNodeTypeEnum('target')));
            return;
        }

        if (selectingObstacle) {
            setType(type.unshift(getNodeTypeEnum('obstacle')));
            return;
        }
    };

    const mouseLeft = () => {
        if (selectingStart || selectingTarget) {
            setType(type.shift())
        }
    };

    const mouseUped = () => {
        if (selectingStart) {
            setSelectingStart(false);
            return;
        }

        if (selectingTarget) {
            setSelectingTarget(false);
            return;
        }

        if (selectingObstacle) {
            setSelectingObstacle(false);
        }
    };

    return (
        <div style={nodeDim} className="node" onMouseDown={mouseDowned} onMouseEnter={mouseEntered} onMouseLeave={mouseLeft} onMouseUp={mouseUped}>
            {type === getNodeTypeEnum("start") && <img className='img-node' style={nodeDim} src={startNodeImg} />}
            {type === getNodeTypeEnum("target") && <img className='img-node' style={nodeDim} src={targetNodeImg} />}
            {type === getNodeTypeEnum("obstacle") && <img className='img-node' style={nodeDim} src={obstacleNodeImg} />}
        </div>
    )
}

export default Node
