import { nodeTypeEnum } from '../../utils/constants.js'
import { sleep } from '../../utils/helper.js'

export const recursiveDivision = async (startCord, targetCord, gridState, setGridState, rows, cols, timeout) => {
    // make border around the screen
    for (var x = 0; x < rows; x++) {
        for (var y = 0; y < cols; y++) {
            const currCord = `${x}:${y}`;
            const nodeStateFunc = gridState[currCord][1];
            if (x === 0 || x === (rows - 1) || y === 0 || y === (cols - 1)) {
                await sleep(timeout);
                let state = [nodeTypeEnum.wall, nodeTypeEnum.none];
                if (currCord === startCord) {
                    state = [nodeTypeEnum.start, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                else if (currCord === targetCord) {
                    state = [nodeTypeEnum.target, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                nodeStateFunc(prevState => state);
                setGridState(prevState => ({ ...prevState, [currCord]: [state, prevState[currCord][1]] }));
            }
        }
    }

    await divideScreen(startCord, targetCord, gridState, setGridState, 1, rows - 1, 1, cols - 1, timeout);
};

const divideScreen = async (startCord, targetCord, gridState, setGridState, startRow, endRow, startCol, endCol, timeout) => {
    const width = endCol - startCol;
    const height = endRow - startRow;

    if (width <= 3 || height <= 3 || startRow) {
        return;
    }

    let orientation = 1; // verical
    if (width < height) {
        // horizontal
        orientation = 0;
    }

    if (direction === 1) {
        // divide horizontally
        const skip = Math.floor((Math.random() * endCol) + startCol);
        const randRow = Math.floor((Math.random() * endRow) + startRow);
        for (let i = startCol; i <= endCol; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${randRow}:${i}`;
            console.log(currCord);
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            setGridState(prevState => ({ ...prevState, [currCord]: [[nodeTypeEnum.wall, nodeTypeEnum.none], prevState[currCord][1]] }));
            await sleep(timeout);
        }

        await divideScreen(startCord, targetCord, gridState, setGridState, startRow, randRow - 1, startCol, endCol, timeout);
        await divideScreen(startCord, targetCord, gridState, setGridState, randRow + 1, endRow, startCol, endCol, timeout);
    }
    else {
        // divide vertically 
        const skip = Math.floor((Math.random() * endCol) + startCol);
        const randCol = startCol + Math.floor((endCol - startCol) / 2);
        for (let i = startRow; i <= endRow; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${i}:${randCol}`;
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            setGridState(prevState => ({ ...prevState, [currCord]: [[nodeTypeEnum.wall, nodeTypeEnum.none], prevState[currCord][1]] }));
            await sleep(timeout);
        }

        await divideScreen(startCord, targetCord, gridState, setGridState, startRow, endRow, startCol, randCol - 1, timeout);
        await divideScreen(startCord, targetCord, gridState, setGridState, startRow, endRow, randCol + 1, endCol, timeout);
    }
};
