import React from 'react'
import Node from './Node.js'

const Grid = ({ rows, cols, nodeSize }) => {
    console.log(rows, cols, nodeSize)
    // fill nodes in the grid
    const nodes = [];
    for (let x = 0; x < rows; x++) {
        for (let y = 0; y < cols; y++) {
            let isStart = (x === 0 && y === 0) ? true : false
            let isTarget = (x === (rows - 1) && y === (cols - 1))
            nodes.push(<Node size={nodeSize} isStart={isStart} isTarget={isTarget} key={[x, y]} />)
        }
    }

    const gridStyle = {
        marginTop: 150,
        width: (cols * nodeSize) + cols,
        height: (rows * nodeSize) + rows
    }
    return (
        <div className='grid' style={gridStyle}>
            { nodes}
        </div >
    )
}

export default Grid
