import { nodeTypeEnum } from '../../utils/constants.js'
import { sleep, getRandomNum } from '../../utils/helper.js'

export const recursiveDivision = async (startCord, targetCord, gridState, setGridState, rows, cols, timeout) => {
    // make border around the screen
    for (var x = 0; x < rows; x++) {
        for (var y = 0; y < cols; y++) {
            const currCord = `${x}:${y}`;
            const nodeStateFunc = gridState[currCord][1];
            if (x === 0 || x === (rows - 1) || y === 0 || y === (cols - 1)) {
                await sleep(timeout);
                let state = [nodeTypeEnum.wall, nodeTypeEnum.none];
                if (currCord === startCord) {
                    state = [nodeTypeEnum.start, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                else if (currCord === targetCord) {
                    state = [nodeTypeEnum.target, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                nodeStateFunc(prevState => state);
                setGridState(prevState => ({ ...prevState, [currCord]: [state, prevState[currCord][1]] }));
            }
        }
    }

    await divide(startCord, targetCord, gridState, setGridState, 1, rows - 1, 1, cols - 1, timeout, rows, cols, -100);
};

const divide = async (startCord, targetCord, gridState, setGridState, startRow, endRow, startCol, endCol, timeout, rows, cols, previousBisection) => {
    const width = endCol - startCol;
    const height = endRow - startRow;

    if (width < 2 || height < 2) {
        return;
    }

    if (width < height) {
        // cutting horizontally
        const skip = getRandomNum(startCol, endCol);
        let randRow = getRandomNum(startRow + 1, endRow - 1);
        while (randRow === previousBisection) {
            randRow = getRandomNum(startRow + 1, endRow - 1);
        }

        for (let i = startCol; i <= endCol; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${randRow}:${i}`;
            console.log(startRow, endRow, currCord);
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            setGridState(prevState => ({ ...prevState, [currCord]: [[nodeTypeEnum.wall, nodeTypeEnum.none], prevState[currCord][1]] }));
            await sleep(timeout);
        }

        if (randRow - 1 > 0) {
            await divideScreen(startCord, targetCord, gridState, setGridState, startRow, randRow - 1, startCol, endCol, timeout, rows, cols, skip);
        }
        if ((randRow + 1) < rows) {
            await divideScreen(startCord, targetCord, gridState, setGridState, randRow + 1, endRow, startCol, endCol, timeout, rows, cols, skip);
        }
    }
    else {
        // divide vertically 
        const skip = getRandomNum(startRow, endRow);
        let randCol = getRandomNum(startCol + 1, endCol - 1);
        while (randCol === previousBisection) {
            randCol = getRandomNum(startCol + 1, endCol - 1);
        }
        console.log(startCol, endCol, randCol)
        for (let i = startRow; i <= endRow; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${i}:${randCol}`;
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            setGridState(prevState => ({ ...prevState, [currCord]: [[nodeTypeEnum.wall, nodeTypeEnum.none], prevState[currCord][1]] }));
            await sleep(timeout);
        }

        if (randCol - 1 > 0) {
            await divideScreen(startCord, targetCord, gridState, setGridState, startRow, endRow, startCol, randCol - 1, timeout, rows, cols, skip);
        }
        if (randCol + 1 < cols) {
            await divideScreen(startCord, targetCord, gridState, setGridState, startRow, endRow, randCol + 1, endCol, timeout, rows, cols, skip);
        }
    }
};
