import { useState } from 'react'

const Reset = ({ resetBoard }) => {
    const [clicked, setClicked] = useState('red');

    const click = () => {
        setClicked('clicked');
    };

    const unclick = () => {
        setClicked('red');
    };

    const hover = () => {
        setClicked('red hover');
    };

    return (
        <div className={`button ${clicked}`} onClick={resetBoard} onMouseDown={click} onMouseUp={unclick} onMouseEnter={hover} onMouseLeave={unclick}>
            Reset Grid
        </div >
    )
}

export default Reset
