const nodeTypeEnum = {
    "none": 0,
    "target": 1,
    "obstacle": 2,
    "start": 3
};
Object.freeze(nodeTypeEnum);

const nodeColor = {
    [nodeTypeEnum.none]: 'transparent',
    [nodeTypeEnum.target]: 'red',
    [nodeTypeEnum.obstacle]: 'black',
    [nodeTypeEnum.start]: 'green',
}
export const getNodeTypeEnum = (key) => {
    return nodeTypeEnum[key];
};
