import Grid from './components/Grid.js'

function App() {
  const nodeSize = 50;
  const rows = parseInt(window.innerHeight / 50) - 4; // leave 2 node spaces on left and right
  const cols = parseInt(window.innerWidth / 50) - 2; // leave 4 node spaces on top and bottom of grid

  return (
    <>
      <Grid rows={rows} cols={cols} nodeSize={nodeSize} />
    </>
  );
}

export default App;
