import { nodeTypeEnum } from '../../utils/constants.js'
import { sleep } from '../../utils/helper.js'

export const recursiveDivision = async (startCord, targetCord, gridState, setGridState, rows, cols, timeout) => {
    // make border around the screen
    for (var x = 0; x < rows; x++) {
        for (var y = 0; y < cols; y++) {
            const currCord = `${x}:${y}`;
            const nodeStateFunc = gridState[currCord][1];
            if (x === 0 || x === (rows - 1) || y === 0 || y === (cols - 1)) {
                await sleep(timeout);
                let state = [nodeTypeEnum.wall, nodeTypeEnum.none];
                if (currCord === startCord) {
                    state = [nodeTypeEnum.start, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                else if (currCord === targetCord) {
                    state = [nodeTypeEnum.target, nodeTypeEnum.wall, nodeTypeEnum.none];
                }
                nodeStateFunc(prevState => state);
            }
        }
    }

    await divideScreen(startCord, targetCord, gridState, 1, rows - 1, 1, cols - 1, timeout, 1);
};

const divideScreen = async (startCord, targetCord, gridState, startRow, endRow, startCol, endCol, timeout, direction) => {
    if (startRow - endRow <= 2 || startCol - endCol <= 2) {
        return;
    }

    if (direction === 1) {
        // divide horizontally
        const skip = Math.floor((Math.random() * endCol) + startCol);
        const halfRow = startRow + Math.floor((startRow + endRow) / 2);
        for (let i = startCol; i < endCol; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${halfRow}:${i}`;
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            await sleep(timeout);
        }

        await divideScreen(startCord, targetCord, gridState, startRow, halfRow - 1, startCol, endCol, timeout, 0);
        await divideScreen(startCord, targetCord, gridState, halfRow + 1, endRow, startCol, endCol, timeout, 0);
    }
    else {
        // divide vertically 
        const skip = Math.floor((Math.random() * endRow) + startRow);
        const halfCol = startCol + Math.floor((startCol + endCol) / 2);
        for (let i = startRow; i < endRow; i++) {
            if (i === skip) {
                continue;
            }
            const currCord = `${i}:${halfCol}`;
            const nodeStateFunc = gridState[currCord][1];
            nodeStateFunc(prevState => [nodeTypeEnum.wall, nodeTypeEnum.none]);
            await sleep(timeout);
        }

        await divideScreen(startCord, targetCord, gridState, startRow, endRow, startCol, halfCol - 1, timeout, 1);
        await divideScreen(startCord, targetCord, gridState, startRow, endRow, halfCol + 1, endCol, timeout, 1);
    }
};
