import { useState } from 'react'

const CustomDropdown = ({ setSelection, items }) => {
    const [dropDownOpen, setDropDownOpen] = useState(false);
    const [selectedVal, setSelectedVal] = useState(items[0]);
    const [selectedId, setSelectedId] = useState(0);
    const toggle = () => {
        setDropDownOpen(prevState => !prevState);
    }

    const madeSelection = (val) => {
        setDropDownOpen(prevState => !prevState);
        setSelectedVal(val);
        setSelection(selectedVal);
    };

    const dropdownItems = [];
    for (const item of items) {
        dropdownItems.push(<div className='dropdown-list-item' onClick={() => { madeSelection(item) }}>
            {item}
        </div>)
    }

    return (
        <div className='dropdown'>
            <div className={`dropdown-header ${dropDownOpen && 'open'}`} onClick={toggle}>
                {selectedVal}
            </div>
            {dropDownOpen &&
                <div className='dropdown-list'>
                    {dropdownItems}
                </div>
            }
        </div >
    )
}

export default CustomDropdown
