import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'
import startNodeImg from '../images/start.png'
import targetNodeImg from '../images/target.png'
import obstacleNodeImg from '../images/obstacle.png'

const Node = ({ nodeId, size, nodeState, setNodeState, draggingSelection, setDraggingSelection }) => {
    const nodeDim = {
        width: size,
        height: size
    };

    const imgDim = {
        width: size * 0.8,
        height: size * 0.8
    };

    const mouseDowned = () => {
        if (nodeState[0] === getNodeTypeEnum('none')) {
            setDraggingSelection(getNodeTypeEnum('obstacle'));
            setNodeState({ [nodeId]: [getNodeTypeEnum('obstacle'), ...gridState[nodeId]] });
            return;
        }

        if (nodeState[0] === getNodeTypeEnum('obstacle')) {
            setGridState({ [nodeId]: gridState[nodeId].slice(1) });
            return;
        }

        setDraggingSelection(nodeState);
    };

    const mouseEntered = () => {
        if (draggingSelection === getNodeTypeEnum('obstacle') && currNodeType === getNodeTypeEnum('none')) {
            setGridState({ [nodeId]: [getNodeTypeEnum('obstacle'), ...gridState[nodeId]] });
        }
        else if (draggingSelection === getNodeTypeEnum('start')) {
            setGridState({ [nodeId]: [getNodeTypeEnum('start'), ...nodeState] });
        }
        else if (draggingSelection === getNodeTypeEnum('target')) {
            setGridState({ [nodeId]: [getNodeTypeEnum('target'), ...nodeState] });
        }
    };

    const mouseLeft = () => {
        if (draggingSelection === getNodeTypeEnum('start') || draggingSelection === getNodeTypeEnum('target')) {
            setGridState({ [nodeId]: nodeState.slice(1) });
            return;
        }
    };

    const mouseUped = () => {
        if (draggingSelection !== getNodeTypeEnum('none')) {
            setDraggingSelection(getNodeTypeEnum('none'));
        }
    };

    return (
        <div style={nodeDim} className="node" >
            {gridState[nodeId][0] === getNodeTypeEnum('start') && <img className='img-node' alt='Start Node' style={imgDim} src={startNodeImg} />}
            {gridState[nodeId][0] === getNodeTypeEnum('target') && <img className='img-node' alt='Target Node' style={imgDim} src={targetNodeImg} />}
            {gridState[nodeId][0] === getNodeTypeEnum('obstacle') && <img className='img-node' alt='Obstacle' style={imgDim} src={obstacleNodeImg} />}
            <div style={{ ...nodeDim, position: 'absolute', top: 0, left: 0 }} onMouseDown={mouseDowned} onMouseUp={mouseUped} onMouseEnter={mouseEntered} onMouseLeave={mouseLeft} />
        </div>
    )
}

export default Node
