import { useState } from 'react'
import React from 'react'

const Node = ({ size, isStart, isTarget }) => {
    const [isStartNode, setStart] = useState(isStart)
    let bColor = 'none'

    if (isStart) {
        bColor = 'green'
    }
    else if (isTarget) {
        bColor = 'red'
    }

    const nodeStyle = {
        width: size,
        height: size,
        backgroundColor: bColor
    }

    return (
        <div style={nodeStyle} className="node" />
    )
}

export default Node
