import { useState } from 'react'

const Reset = ({ resetBoard }) => {
    const [clicked, setClicked] = useState(false);

    const click = () => {
        setClicked(true);
    };

    const unclick = () => {
        setClicked(false);
    };

    return (
        <div className={`button ${clicked ? 'clicked' : 'red'}`} onClick={resetBoard} onMouseDown={click} onMouseUp={unclick}>
            Reset Grid
        </div >
    )
}

export default Reset
