import { useState } from 'react'

const Reset = ({ resetBoard }) => {
    const [clicked, setClicked] = useState('');

    const click = () => {
        setClicked('clicked');
    };

    const unclick = () => {
        setClicked('');
    };

    return (
        <div className={`button red ${clicked}`} onClick={resetBoard} onMouseDown={click} onMouseUp={unclick}>
            Reset Grid
        </div >
    )
}

export default Reset
