import { useState } from 'react'
import React from 'react'
import { getNodeTypeEnum } from '../utils/util.js'
import startNodeImg from '../images/start.png'
import targetNodeImg from '../images/target.png'
import obstacleNodeImg from '../images/obstacle.png'

const Node = ({ nodeId, size, nodeState, setNodeState, draggingSelection, setDraggingSelection }) => {
    let tempState = [nodeState];
    if (nodeState !== getNodeTypeEnum('none')) {
        tempState.push(getNodeTypeEnum('none'));
    }

    const [state, setState] = useState(tempState);

    const nodeDim = {
        width: size,
        height: size
    };

    const imgDim = {
        width: size * 0.8,
        height: size * 0.8
    };

    const mouseDowned = () => {
        const currNodeState = state;

        if (currNodeState[0] === getNodeTypeEnum('none')) {
            setState(prevState => ([getNodeTypeEnum('obstacle'), ...prevState]));
            setDraggingSelection(getNodeTypeEnum('obstacle'));
            setNodeState(nodeId, getNodeTypeEnum('obstacle'));
            return;
        }
        else if (currNodeState[0] === getNodeTypeEnum('obstacle')) {
            setState(prevState => (prevState.slice(1)));
            setNodeState(nodeId, currNodeState[1]);
            return;
        }

        setDraggingSelection(currNodeState[0]);
    };

    const mouseEntered = () => {
        let currNodeState = state;

        if (draggingSelection === getNodeTypeEnum('obstacle') && currNodeState[0] === getNodeTypeEnum('none')) {
            setState(prevState => ([getNodeTypeEnum('obstacle'), ...prevState]));
            setNodeState(nodeId, getNodeTypeEnum('obstacle'));
        }
        else if (draggingSelection === getNodeTypeEnum('start')) {
            setState(prevState => ([getNodeTypeEnum('start'), ...prevState]));
            setNodeState(nodeId, getNodeTypeEnum('start'));
        }
        else if (draggingSelection === getNodeTypeEnum('target')) {
            setState(prevState => ([getNodeTypeEnum('target'), ...prevState]));
            setNodeState(nodeId, getNodeTypeEnum('target'));
        }
    };

    const mouseLeft = () => {
        let currNodeState = state;

        if (draggingSelection === getNodeTypeEnum('start') || draggingSelection === getNodeTypeEnum('target')) {
            setNodeState(nodeId, currNodeState[1]);
            setState(currNodeState.slice(1));
            return;
        }
    };

    const mouseUped = () => {
        if (draggingSelection !== getNodeTypeEnum('none')) {
            setDraggingSelection(getNodeTypeEnum('none'));
        }
    };

    return (
        <div style={nodeDim} className="node" >
            {state[0] === getNodeTypeEnum('start') && <img className='img-node' alt='Start Node' style={imgDim} src={startNodeImg} />}
            {state[0] === getNodeTypeEnum('target') && <img className='img-node' alt='Target Node' style={imgDim} src={targetNodeImg} />}
            {state[0] === getNodeTypeEnum('obstacle') && <img className='img-node' alt='Obstacle' style={imgDim} src={obstacleNodeImg} />}
            <div style={{ ...nodeDim, position: 'absolute', top: 0, left: 0 }} onMouseDown={mouseDowned} onMouseUp={mouseUped} onMouseEnter={mouseEntered} onMouseLeave={mouseLeft} />
        </div>
    )
}

export default Node
